mapper.insert:
INSERT
INTO BUS_INFO
(
	SHOP_NO
  , SHOP_CATE
  , SHOP_ADD
  , SHOP_INTRO
  , SHOP_TIME1
  , TOP_MENU
  , MENU_INFO
  , SHOP_PHONE
  , USER_ID
  , AD_CASH
  , SHOP_TIME2
  , SHOP_TIME3
  , T_NO
  , SHOP_NAME

)
VALUES
(
	SEQ_SHOPNO.NEXTVAL
  , #{shopCate}
  , #{shopAdd}
  , #{shopIntro}
  , #{shopTime1}
  , #{topMenu}
  , #{menuInfo}
  , #{shopPhone}
  , #{userId}
  , DEFAULT
  , #{shopTime2}
  , #{shopTime3}
  , #{tNo}
  , #{shopName}
)

INSERT
INTO NEWS
(
	N_NO
  , NEWS_TITLE
  , SHOP_NEWS
  , SHOP_NO
  , T_NO
  , NSTATUS


)
VALUES
(
	SEQ_NNO.NEXTVAL
  , #{newsTitle}
  , #{shopNews}
  , #{shopNo}
  , #{tNo}
  , DEFAULT

)

mapper.update:
UPDATE
	BUS_INFO
SET
 	SHOP_CATE = #{shopCate}
  , SHOP_ADD  = #{shopAdd}
  , SHOP_INTRO = #{shopIntro}
  , SHOP_TIME1 = #{shopTime1}
  , TOP_MENU   = #{topMenu}	
  , MENU_INFO  = #{menuInfo}
  , SHOP_PHONE = #{shopPhone}
  , SHOP_TIME2 = #{shopTime2}
  , SHOP_TIME3 = #{shopTime3}
  , SHOP_NAME  = #{shopName}
WHERE
	SHOP_NO	 = #{shopNo}

mapper.insert:

		INSERT INTO ADDFILE(
			F_NO
		  , ORIGIN_NAME
		  , CHANGE_NAME
          , STATUS
          , FILE_LEVEL
		)
		VALUES
		(
			F_SEQ.NEXTVAL
		  , #{originName}
		  , #{changeName}
          , DEFAULT
          , #{file_level}

		)

INSERT ALL INTO FAVORITE ( SHOP_NO , USER_ID , F_DATE
) VALUES ( #{shopNo} , #{userId} , DEFAULT
) INTO ALARM ( ALARM_NO , ALARM_TYPE , ALARM_CONTENT , USER_ID , ALARM_DATE , A_STATUS ) VALUES ( A_SEQ.NEXTVAL , 3 , CONCAT( #{nickName},'님이 회원님의 가게를 단골 등록 하였습니다.') , #{buserId} , SYSDATE , DEFAULT
) SELECT * FROM DUAL

INSERT ALL INTO REVIEW ( REVIEW_NO , STATUS , CONTENT , REVIEW_DATE , GRADE , USER_ID ) VALUES ( R_SEQ.NEXTVAL , DEFAULT , #{content} , DEFAULT , #{grade} , #{userId} ) INTO BUS_REVIEW ( SHOP_NO , REVIEW_NO ) VALUES ( #{shopNo} , R_SEQ.CURRVAL
) INTO ALARM ( ALARM_NO , ALARM_TYPE , ALARM_CONTENT , USER_ID , ALARM_DATE , A_STATUS ) VALUES ( A_SEQ.NEXTVAL , 4 , CONCAT( #{shopName},' 가게의 후기가 등록 되었습니다.') , #{buserId} , SYSDATE , DEFAULT
)
SELECT * FROM DUAL

mapper.update:
UPDATE
	BUS_INFO
SET
	AD_CASH = AD_CASH + #{payment}
WHERE
	SHOP_NO	 = #{shopNo}

UPDATE
	BUS_INFO
SET
	AD_CASH = AD_CASH - #{cash}
WHERE
	SHOP_NO = #{shopNo}

UPDATE
	AD
SET
	VIEW_CNT = VIEW_CNT + #{viewCnt}
WHERE
	SHOP_NO	= #{shopNo}

mapper.delete:
DELETE FROM FAVORITE
WHERE 
	SHOP_NO = #{shopNo}
AND 
	USER_ID = #{userId}
