mapper.select:
SELECT C_NO
FROM GOODS_CATE
WHERE L_FILTER = #{lfilter}
 AND M_FILTER = #{mfilter}
 AND S_FILTER = #{sfilter}

SELECT COUNT(*) FROM (SELECT * FROM GOODS G, TOWN T, GOODSFILE GF, ADDFILE F WHERE G.G_PLACE = T.T_NO AND G.G_NO = GF.G_NO AND GF.F_NO = F.F_NO AND F.FILE_LEVEL = 1) WHERE G_STATUS = 'Y'
AND ADDRESS_1 = #{address_1}
AND ADDRESS_2 = #{address_2}
AND ADDRESS_3 =#{address_3}

SELECT G_NO , G_TITLE , G_PRICE , CHANGE_NAME , CREATE_DATE , NICKNAME FROM (SELECT G_PRICE , G_TITLE , CREATE_DATE , CHANGE_NAME , G.G_NO G_NO , G_STATUS , G.USER_ID USER_ID , U.NICKNAME NICKNAME , ADDRESS_1 , ADDRESS_2 , ADDRESS_3 FROM GOODS G, TOWN T, GOODSFILE GF, ADDFILE F, USER_INFO U WHERE G.G_PLACE = T.T_NO AND G.G_NO = GF.G_NO AND GF.F_NO = F.F_NO AND G.USER_ID = U.USER_ID AND F.FILE_LEVEL = 1) WHERE G_STATUS = 'Y'
AND ADDRESS_1 = #{address_1}
AND ADDRESS_2 = #{address_2}
AND ADDRESS_3 =#{address_3}
ORDER BY CREATE_DATE DESC

SELECT COUNT(*) FROM (SELECT * FROM GOODS G, TOWN T, GOODSFILE GF, ADDFILE F WHERE G.G_PLACE = T.T_NO AND G.G_NO = GF.G_NO AND GF.F_NO = F.F_NO AND F.FILE_LEVEL = 1) WHERE G_STATUS = 'Y'
AND ADDRESS_1 = #{myTown.address_1}
AND ADDRESS_2 = #{myTown.address_2}
AND ADDRESS_3 =#{myTown.address_3}
AND C_NO IN (SELECT C_NO FROM GOODS_CATE WHERE L_FILTER =#{cate})

SELECT G_NO , G_TITLE , G_PRICE , CHANGE_NAME , CREATE_DATE , NICKNAME FROM (SELECT G_PRICE , G_TITLE , CREATE_DATE , CHANGE_NAME , G.G_NO G_NO , G.C_NO C_NO , G_STATUS , U.NICKNAME NICKNAME , ADDRESS_1 , ADDRESS_2 , ADDRESS_3 FROM GOODS G, TOWN T, GOODSFILE GF, ADDFILE F, USER_INFO U WHERE G.G_PLACE = T.T_NO AND G.G_NO = GF.G_NO AND GF.F_NO = F.F_NO AND G.USER_ID = U.USER_ID AND F.FILE_LEVEL = 1) WHERE G_STATUS = 'Y'
AND ADDRESS_1 = #{myTown.address_1}
AND ADDRESS_2 = #{myTown.address_2}
AND ADDRESS_3 =#{myTown.address_3}
AND C_NO IN (SELECT C_NO FROM GOODS_CATE WHERE L_FILTER =#{cate}) ORDER BY CREATE_DATE DESC

SELECT COUNT(*)
  FROM LIKE_GOODS
 WHERE G_NO = #{gno}
   AND USER_ID = #{user_id}

mapper.delete:
DELETE FROM LIKE_GOODS
 WHERE G_NO = #{gno}
   AND USER_ID = #{user_id}

mapper.select:
	SELECT USER_ID
	  FROM LIKE_GOODS
     WHERE G_NO =#{gno}

mapper.update:
UPDATE GOODS
  SET G_PLACE = (SELECT T_NO FROM MY_TOWN WHERE USER_ID = #{user_id} AND MYTOWN_TYPE=#{gplace})
	, G_PRICE = #{gprice}
	, G_CONDITION = #{gcondition}
	, G_COMMENT = #{gcomment}
	, C_NO = #{cno}
	, G_TITLE = #{gtitle}
	WHERE G_NO = #{gno}

UPDATE REPLY
SET R_STATUS = 'N'
WHERE R_NO = #{rno}

UPDATE GOODS
SET G_STATUS = 'R'
WHERE G_NO = #{gno}

UPDATE GOODS
SET G_STATUS = 'N'
WHERE G_NO = #{gno}

UPDATE GOODS
SET G_STATUS = 'N'
WHERE G_NO = #{gno}

mapper.select:

SELECT COUNT(*)
FROM GOODS
WHERE G_TITLE LIKE #{searchb}


		SELECT 
			   COUNT(*)
		 FROM (SELECT *
			   FROM GOODS G, TOWN T, GOODSFILE GF, ADDFILE F
			   WHERE G.G_PLACE = T.T_NO
			   AND G.G_NO = GF.G_NO
			   AND GF.F_NO = F.F_NO
			   AND F.FILE_LEVEL = 1)
		WHERE G_STATUS = 'Y'
        AND G_TITLE LIKE #{searchb}

		AND ADDRESS_1 = #{myTown.address_1}


		AND ADDRESS_2 = #{myTown.address_2}


		AND ADDRESS_3 =#{myTown.address_3}

SELECT G_NO , G_TITLE , G_PRICE , CHANGE_NAME , CREATE_DATE , NICKNAME FROM (SELECT G_PRICE , G_TITLE , CREATE_DATE , CHANGE_NAME , G.G_NO G_NO , G.C_NO C_NO , G_STATUS , U.NICKNAME NICKNAME , ADDRESS_1 , ADDRESS_2 , ADDRESS_3 FROM GOODS G, TOWN T, GOODSFILE GF, ADDFILE F, USER_INFO U WHERE G.G_PLACE = T.T_NO AND G.G_NO = GF.G_NO AND GF.F_NO = F.F_NO AND G.USER_ID = U.USER_ID AND F.FILE_LEVEL = 1) WHERE G_STATUS = 'Y' AND G_TITLE LIKE #{searchb}
AND ADDRESS_1 = #{myTown.address_1}
AND ADDRESS_2 = #{myTown.address_2}
AND ADDRESS_3 =#{myTown.address_3}
ORDER BY CREATE_DATE DESC

SELECT COUNT(*)
  FROM DEAL_PAYMENT D JOIN GOODS G ON(D.G_NO = G.G_NO)
 WHERE G.USER_ID = #{seller}
   AND D.USER_ID = #{user_id}

select g_no
 from goods_report
  where r_no = #{re_no}
